mapper.select:
SELECT * FROM ( SELECT s.`s_id`,c.c_name,s.s_num,s.m_id AS s_mid,s.s_title,s.s_txt,s.s_img,s.s_fmImg,s.s_click,s.s_price,s.s_price_yuan,s.s_state,s.s_audit,m.m_id,m.m_name,m.m_address,m.m_phone,m.m_uid,u.u_id,u.u_name,u.u_phone FROM ((scenery s INNER JOIN mark m ON s.`m_id` = m.`m_id`) INNER JOIN USER u ON m.`m_uid`=u.`u_id`) INNER JOIN city c ON s.s_city = c.c_id
s_id LIKE concat('%',#{search},'%') or c_name LIKE concat('%',#{search},'%') or s_num LIKE concat('%',#{search},'%') or s_title LIKE concat('%',#{search},'%') or s_txt LIKE concat('%',#{search},'%') or s_click LIKE concat('%',#{search},'%') or s_price LIKE concat('%',#{search},'%') or s_price_yuan LIKE concat('%',#{search},'%') or m_name LIKE concat('%',#{search},'%') or m_phone LIKE concat('%',#{search},'%') or u_name LIKE concat('%',#{search},'%')
) a
a.`s_state` != 0
a.`s_state` = #{s_state}
order by s_audit desc

select * from (
  SELECT s.`s_id`,s.s_num,s.m_id AS s_mid,s.s_title,s.s_txt,s.s_img,s.s_fmImg,s.s_click,s.s_price,s.s_price_yuan,s.s_state,s.s_audit,m.m_id,m.m_name,m.m_address,m.m_phone,m.m_uid,u.u_id,u.u_name,u.u_phone FROM (scenery s INNER JOIN mark m ON s.`m_id` = m.`m_id`) INNER JOIN USER u ON m.`m_uid`=u.`u_id`) a
  where a.s_id = #{s_id}
